mapper.select:
/* QUERY ID : serviceList */
   SELECT svc_id svcId,
          svc_name svcName,
          svc_desc svcDesc,
          svc_prefix svcPrefix,
          is_dummy isDummy,
          is_menu isMenu,
          upper_svc upperSvc,
          svc_url svcUrl,
          create_dt createDt,
          creator creator,
          auth_required authRequired,
          use_yn useYn
     FROM FRWK_CMM_SVCINFO
    WHERE 1 = 1 
      AND svc_name like #{svcName} 
      AND use_yn like #{useYn} 
      AND is_menu like #{isMenu} 
      AND svc_prefix like #{svcPrefix} 
      AND auth_required like #{authRequired}
