mapper.select:
/* QUERY ID : getCurrBoothRentFee */ SELECT
rent_amount rentAmount rent_percent rentPercent
FROM ptm_rent_fee WHERE rent_year = DATE_FORMAT(CURRENT_DATE,'%Y') AND rent_month = DATE_FORMAT(CURRENT_DATE,'%m') AND booth_id = #{boothId}

mapper.update:
/* QUERY ID : modifyRentFeeInfo */
     UPDATE ptm_rent_fee SET
             rent_amount  = #{rentAmount},         
             rent_percent  = #{rentPercent},         
            modify_dt  = CURRENT_TIMESTAMP
      WHERE booth_id = #{boothId} AND rent_year = #{rentYear} AND rent_month = #{rentMonth}
